import React, { useState } from "react";
import "./App.css";
function Signup() {
  const [roleRadio, setRoleRadio] = useState(""); // radio sélectionné
  const [roleSelect, setRoleSelect] = useState("user"); // valeur du select

  const handleRadioChange = (e) => {
    setRoleRadio(e.target.value);

    // Si on clique sur "autre", on garde la valeur select
    // Sinon on reset la valeur select à "user" (ou ce que tu veux)
    if (e.target.value !== "autre") {
      setRoleSelect(e.target.value);
    }
  };

  const handleSelectChange = (e) => {
    setRoleSelect(e.target.value);
  };

  return (
    <div className="signup-container">
      <form className="signup-form" >
        <h2>Inscription</h2>
        <hr />
        <br />

        <p align="center">Vous êtes :</p>
        <div className="role-selection">
           <p>Choisissez un rôle :</p>
           <label className="radio-option">
              <input
                type="radio"
                name="role"
                value="superadmin"
                checked={roleRadio === "superadmin"}
                onChange={handleRadioChange}
              />
              Superadmin
           </label>

           <label className="radio-option">
             <input
                type="radio"
                name="role"
                value="autre"
                checked={roleRadio === "autre"}
                onChange={handleRadioChange}
             />
              Autre
           </label>

           {roleRadio === "autre" && (
             <select value={roleSelect} onChange={handleSelectChange} className="role-select">
                <option value="user">Utilisateur</option>
                <option value="admin">Administrateur</option>
             </select>
           )}

           <p>Rôle sélectionné : {roleRadio === "autre" ? roleSelect : roleRadio}</p>
        </div>
      </form>
    </div>
  );
}

export default Signup;
