    const [roleRadio, setRoleRadio] = useState(""); // radio sélectionné
    const [roleSelect, setRoleSelect] = useState("user");
    const handleRadioChange = (e) => {
      setRoleRadio(e.target.value);

    // Si on clique sur "autre", on garde la valeur select
    // Sinon on reset la valeur select à "user" (ou ce que tu veux)
      if (e.target.value !== "autre") {
        setRoleSelect(e.target.value);
      }
    };
    const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setRoleRadio(e.target.value);
    // 🔍 Vérification des champs

    if (e.target.value !== "autre") {
      setRoleSelect(e.target.value);
    }
    const handleSelectChange = (e) => {
      setRoleSelect(e.target.value);
    };
    if (!username || !email || !password || !confirmPassword) {
      setError("Tous les champs sont obligatoires");
      return;
    }

    if (password !== confirmPassword) {
      setError("Les mots de passe ne correspondent pas");
      return;
    }

    try {
      const response = await fetch("http://localhost:4000/api/auth/register", {
        method: "POST",
