import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom"; 
import "./users.css";
function UsersList() {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");
  const [currentUserRole, setCurrentUserRole] = useState("Utilisateur");
  const [editingUserId, setEditingUserId] = useState(null);
  const navigate = useNavigate();
  const roleOptions = [
    { value: "Utilisateur", label: "Utilisateur" },
    { value: "Administrateur", label: "Administrateur" },
  ];

  useEffect(() => {
    const role = localStorage.getItem("role") || "Utilisateur";
    const roleApi = role === "Administrateur" ? "admin" : "user";
    setCurrentUserRole(roleApi);
    console.log(role);
    fetch("http://localhost:4000/api/auth/users")
      .then((res) => {
        if (!res.ok) {
          throw new Error("Erreur lors du chargement des utilisateurs");
        }
        return res.json();
      })
      .then((data) => setUsers(data))
      .catch((err) => setError(err.message));
  }, []);
  const displayRole = (role) => {
    const roleMap = {
       user: "Utilisateur",
       admin: "Administrateur"
    };
    return roleMap[role] || role;
  };
  const convertRoleForDisplay = (role) => {
    return role === "admin" ? "Administrateur" : "Utilisateur";
  };
  const handleDelete = async (userId) => {
    try {
      const res = await fetch(`http://localhost:4000/api/auth/users/${userId}`, {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ role: currentUserRole }),
      });
      const data = await res.json();
      if (res.ok) {
        setUsers(users.filter((u) => u.id !== userId));
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error("Erreur suppression:", error);
    }
  };

  const handleRoleChange = async (userId, newRole) => {
    try {
      const res = await fetch(`http://localhost:4000/api/auth/users/${userId}/role`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ role: currentUserRole, newRole }),
      });
      const data = await res.json();
      if (res.ok) {
        setUsers(users.map((user) => (user.id === userId ? { ...user, role:convertRoleForDisplay(newRole) } : user)));
        setEditingUserId(null);
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error("Erreur modification de r√¥le:", error);
    }
  };
  const handleLogout = () => {
    localStorage.clear();  // vide localStorage (token, role, etc.)
    navigate("/");    // redirige vers la page login (ajuste le chemin si besoin)
  };  
  return (
    <div className="users-container">
      <div className="users-header">
        <button className="logout-button" onClick={handleLogout}>Logout</button>
      </div>
      <h2>Liste des Utilisateurs</h2>
      {error && <p className="error">{error}</p>}
      <table className="users-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Email</th>
            <th>R√¥le</th>
               {currentUserRole === "admin" && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {users.length > 0 ? (
            users.map((user) => (
              <tr key={user.id}>
                 <td>{user.id}</td>
                 <td>{user.email}</td>
                 <td>
                    {editingUserId === user.id ? (
                    <select
                       value={user.role === "Utilisateur" ? "user" : user.role === "Administrateur" ? "admin" : user.role}
                       onChange={(e) => handleRoleChange(user.id, e.target.value)} 
                    >
                      <option value="user">Utilisateur</option>
                      <option value="admin">Administrateur</option>
                    </select>
                   ) : (
                     displayRole(user.role)
                   )}
                 </td>
                   {currentUserRole === "admin" && (
                     <td>
                       <button onClick={() => setEditingUserId(user.id)}>‚úèÔ∏è</button>
                       <button onClick={() => handleDelete(user.id)}>üóëÔ∏è</button>
                     </td>
                   )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={currentUserRole === "Administrateur" ? 4 : 3}>Aucun utilisateur trouv√©</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default UsersList;
