import React, { useState } from "react";
import "./signup.css";
import { Link } from "react-router-dom";

function Signup() {
  const [roleRadio, setRoleRadio] = useState("");
  const [roleSelect, setRoleSelect] = useState("user");
  const [username, setUsername] = useState("");

  const handleRadioChange = (e) => {
    setRoleRadio(e.target.value);
    if (e.target.value !== "autre") {
      setRoleSelect(e.target.value);
    }
  };

  const handleSelectChange = (e) => {
    setRoleSelect(e.target.value);
  };

  return (
    <div className="signup-container">
      <form className="signup-form">
        <h2>Inscription</h2>
        <hr />
        <p className="vous-etes">Vous êtes :</p>

        <div className="radio-group">
          <label className="radio-option">
            <input
              type="radio"
              name="role"
              value="superadmin"
              checked={roleRadio === "superadmin"}
              onChange={handleRadioChange}
            />
            Superadmin
          </label>

          <label className="radio-option">
            <input
              type="radio"
              name="role"
              value="autre"
              checked={roleRadio === "autre"}
              onChange={handleRadioChange}
            />
            Autre
          </label>
        </div>

        {roleRadio === "autre" && (
          <div className="select-container">
            <select
              value={roleSelect}
              onChange={handleSelectChange}
              className="role-select"
            >
              <option value="user">Utilisateur</option>
              <option value="admin">Administrateur</option>
            </select>
          </div>
        )}

        { /* <div className="role-selected">
          Rôle sélectionné : {roleRadio === "autre" ? roleSelect : roleRadio || "Aucun"}
        </div> */}

        <p className="vous-etes">Nom d'utilisateur :</p>
        <div>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Entrez votre nom"
            className="username-input"
          />
        </div>

        <p className="vous-etes">Email :</p>
        <div>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Entrez votre nom"
            className="username-input"
          />
        </div>

        <p className="vous-etes">Password :</p>
        <div>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Entrez votre nom"
            className="username-input"
          />
        </div>

        <p className="vous-etes">Confirm Password :</p>
        <div>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Entrez votre nom"
	    className="username-input"
          />
        </div>

        <button type="submit" className="submit-button">S'inscrire</button>

          <p>
          Vous avez un compte ?{" "}
          <Link
            to="/"
            style={{ color: "blue", cursor: "pointer", textDecoration: "underline" }}
          >
            Connectez-vous
          </Link>
        </p>
      </form>
    </div>
  );
}

export default Signup;
