import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "./users.css";

function UsersList() {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");
  const [currentUserRole, setCurrentUserRole] = useState();
  const [editingUserId, setEditingUserId] = useState();
  const [filterText, setFilterText] = useState("");
  const navigate = useNavigate();
  const location = useLocation();

  const RoleFromNavigate = location.state?.role || "user";

  useEffect(() => {
    setCurrentUserRole(RoleFromNavigate);
    fetch("http://localhost:4000/api/auth/users")
      .then((res) => {
        if (!res.ok) {
          throw new Error("Erreur lors du chargement des utilisateurs");
        }
        return res.json();
      })
      .then((data) => {
        setUsers(data);
      })
      .catch((err) => setError(err.message));
  }, []);

  const displayRole = (role) => {
    const roleMap = {
      user: "Utilisateur",
      admin: "Administrateur",
      superadmin: "Super Admin"
    };
    return roleMap[role] || role;
  };

  const handleDelete = async (userId) => {
    try {
      const res = await fetch(`http://localhost:4000/api/auth/users/${userId}`, {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ role: currentUserRole }),
      });
      const data = await res.json();
      if (res.ok) {
        setUsers(users.filter((u) => u.id !== userId));
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error("Erreur suppression:", error);
    }
  };

  const handleRoleChange = async (userId, newRole) => {
    try {
      const res = await fetch(`http://localhost:4000/api/auth/users/${userId}/role`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ role: newRole }),
      });
      const data = await res.json();
      if (res.ok) {
        setUsers(users.map((user) =>
          user.id === userId ? { ...user, role: newRole } : user
        ));
        setEditingUserId(null);
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error("Erreur modification de rÃ´le:", error);
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate("/");
  };

  return (
    <div className="users-container">
      <div className="users-header">
        <button className="logout-button" onClick={handleLogout}>Logout</button>
      </div>
      <h2>Liste des Utilisateurs</h2>
      {error && <p className="error">{error}</p>}

      <input
        type="text"
        placeholder="Rechercher par email..."
        value={filterText}
        onChange={(e) => setFilterText(e.target.value)}
        className="filter-input"
      />

      <table className="users-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Email</th>
            <th>RÃ´le</th>
            {(currentUserRole === "admin" || currentUserRole === "superadmin") && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {users.length > 0 ? (
            users
              .filter((user) =>
                user.email.toLowerCase().includes(filterText.toLowerCase())
              )
              .map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.email}</td>
                  <td>
                    {editingUserId === user.id ? (
                      <select
                        value={user.role}
                        onChange={(e) => handleRoleChange(user.id, e.target.value)}
                      >
                        <option value="user">Utilisateur</option>
                        <option value="admin">Administrateur</option>
                        <option value="superadmin">Super Admin</option>
                      </select>
                    ) : (
                      displayRole(user.role)
                    )}
                  </td>
                  {(currentUserRole === "admin" || currentUserRole === "superadmin") && (
                    <td>
                      <button onClick={() => setEditingUserId(user.id)}>âï¸</button>
                      <button onClick={() => handleDelete(user.id)}>ðï¸</button>
                    </td>
                  )}
                </tr>
              ))
          ) : (
            <tr>
              <td colSpan={(currentUserRole === "admin" || currentUserRole === "superadmin") ? 4 : 3}>
                Aucun utilisateur trouvÃ©
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default UsersList;
